class WeatherData {
  num? latitude;
  num? longitude;
  num? generationtimeMs;
  num? utcOffsetSeconds;
  String? timezone;
  String? timezoneAbbreviation;
  num? elevation;
  CurrentWeather? currentWeather;
  HourlyUnits? hourlyUnits;
  Hourly? hourly;

  WeatherData({
    this.latitude,
    this.longitude,
    this.generationtimeMs,
    this.utcOffsetSeconds,
    this.timezone,
    this.timezoneAbbreviation,
    this.elevation,
    this.currentWeather,
    this.hourlyUnits,
    this.hourly,
  });

  String get weatherImagePath {
    print(currentWeather?.weathercode);
    switch (currentWeather?.weathercode) {
      // 0	Clear sky
      case 0:
        return Assets.images.weather.sun.path;
      // 1, 2, 3	Mainly clear, partly cloudy, and overcast
      case 1:
      case 2:
      case 3:
        return Assets.images.weather.cloudy.path;
      // 51, 53, 55	Drizzle: Light, moderate, and dense intensity
      // 61, 63, 65	Rain: Slight, moderate and heavy intensity
      // 80, 81, 82	Rain showers: Slight, moderate, and violent
      case 51:
      case 53:
      case 55:
      case 61:
      case 63:
      case 65:
      case 81:
      case 82:
      case 83:
        return Assets.images.weather.rain.path;
      // 71, 73, 75	Snow fall: Slight, moderate, and heavy intensity
      // 77	Snow grains
      case 71:
      case 73:
      case 75:
      case 77:
        return Assets.images.weather.snow.path;
      default:
        return Assets.images.weather.unknown.path;
    }
  }

  WeatherData.fromJson(dynamic json) {
    latitude = json['latitude'];
    longitude = json['longitude'];
    generationtimeMs = json['generationtime_ms'];
    utcOffsetSeconds = json['utc_offset_seconds'];
    timezone = json['timezone'];
    timezoneAbbreviation = json['timezone_abbreviation'];
    elevation = json['elevation'];
    currentWeather = json['current_weather'] != null
        ? CurrentWeather.fromJson(json['current_weather'])
        : null;
    hourlyUnits = json['hourly_units'] != null
        ? HourlyUnits.fromJson(json['hourly_units'])
        : null;
    hourly = json['hourly'] != null ? Hourly.fromJson(json['hourly']) : null;
  }

  WeatherData copyWith({
    num? latitude,
    num? longitude,
    num? generationtimeMs,
    num? utcOffsetSeconds,
    String? timezone,
    String? timezoneAbbreviation,
    num? elevation,
    CurrentWeather? currentWeather,
    HourlyUnits? hourlyUnits,
    Hourly? hourly,
  }) =>
      WeatherData(
        latitude: latitude ?? this.latitude,
        longitude: longitude ?? this.longitude,
        generationtimeMs: generationtimeMs ?? this.generationtimeMs,
        utcOffsetSeconds: utcOffsetSeconds ?? this.utcOffsetSeconds,
        timezone: timezone ?? this.timezone,
        timezoneAbbreviation: timezoneAbbreviation ?? this.timezoneAbbreviation,
        elevation: elevation ?? this.elevation,
        currentWeather: currentWeather ?? this.currentWeather,
        hourlyUnits: hourlyUnits ?? this.hourlyUnits,
        hourly: hourly ?? this.hourly,
      );
  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['latitude'] = latitude;
    map['longitude'] = longitude;
    map['generationtime_ms'] = generationtimeMs;
    map['utc_offset_seconds'] = utcOffsetSeconds;
    map['timezone'] = timezone;
    map['timezone_abbreviation'] = timezoneAbbreviation;
    map['elevation'] = elevation;
    if (currentWeather != null) {
      map['current_weather'] = currentWeather?.toJson();
    }
    if (hourlyUnits != null) {
      map['hourly_units'] = hourlyUnits?.toJson();
    }
    if (hourly != null) {
      map['hourly'] = hourly?.toJson();
    }
    return map;
  }
}

